import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/logout', {
        method: 'POST',
        credentials: 'include', // important for sending session cookies
      });

      if (response.ok) {
        navigate('/login'); // Redirect to login after logout
      } else {
        console.error('Logout failed');
      }
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 to-purple-200 px-4">
      <div className="bg-white p-10 rounded-3xl shadow-2xl text-center max-w-md w-full">
        <h1 className="text-4xl font-bold mb-6 text-gray-800">Welcome to Account Manager</h1>
        <p className="text-gray-600 mb-8">Securely manage your cards and passwords in one place.</p>
        <div className="space-y-4">
          <Link to="/cards">
            <button className="w-full py-3 bg-blue-600 text-white rounded-xl shadow hover:bg-blue-700 transition">
              Manage Cards
            </button>
          </Link>
          <Link to="/passwords">
            <button className="w-full py-3 bg-green-600 text-white rounded-xl shadow hover:bg-green-700 transition">
              Manage Passwords
            </button>
          </Link>
          <button
            onClick={handleLogout}
            className="w-full py-3 bg-red-500 text-white rounded-xl shadow hover:bg-red-600 transition"
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
